mapper.select:
SELECT GENETIC_PROFILE.STABLE_ID AS molecularProfileId, MUTATION_COUNT_BY_KEYWORD.KEYWORD AS keyword, MUTATION_COUNT_BY_KEYWORD.ENTREZ_GENE_ID AS entrezGeneId, MUTATION_COUNT_BY_KEYWORD.KEYWORD_COUNT AS numberOfSamplesWithKeyword, MUTATION_COUNT_BY_KEYWORD.GENE_COUNT AS numberOfSamplesWithMutationInGene FROM MUTATION_COUNT_BY_KEYWORD INNER JOIN GENETIC_PROFILE ON MUTATION_COUNT_BY_KEYWORD.GENETIC_PROFILE_ID = GENETIC_PROFILE.GENETIC_PROFILE_ID WHERE GENETIC_PROFILE.STABLE_ID = #{molecularProfileId}
AND
MUTATION_COUNT_BY_KEYWORD.ENTREZ_GENE_ID = #{entrezGeneIds[${i}]} AND MUTATION_COUNT_BY_KEYWORD.KEYWORD = #{keywords[${i}]}
MUTATION_COUNT_BY_KEYWORD.ENTREZ_GENE_ID = #{entrezGeneIds[${i}]} AND MUTATION_COUNT_BY_KEYWORD.KEYWORD IS NULL
